double Vector::findMax() {
    if (this->length == 0) {
        cout << "The vector is empty,can not get sum" << endl;
        return 0;
    }
    double ans = this->vector[0].real();
    for (int i = 0; i < length; ++i) {
        ans = max(ans, this->vector[i].real());
    }
    return ans;
}
